class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        } else if (k == 2) {
            if (n <= 2) return string(n, '8');
            return "8" + string(n - 2, '9') + "8";
        } else if (k == 4) {
            if (n <= 4) return string(n, '8');
            return "88" + string(n - 4, '9') + "88";
        } else if (k == 8) {
            if (n <= 6) return string(n, '8');
            return "888" + string(n - 6, '9') + "888";
        } else if (k == 5) {
            if (n <= 2) return string(n, '5');
            return "5" + string(n - 2, '9') + "5";
        } else if (k == 6) {
            if (n <= 2) return string(n, '6');
            string ans = "8" + string(n - 2, '9') + "8";
            int sum_digits = 9 * n - 2;
            if (n % 2 == 1) {
                ans[n / 2] -= sum_digits % 3;
                return ans;
            } else {
                if (sum_digits % 3 == 0) return ans;
                ans[n / 2 - 1] -= 3 - sum_digits % 3;
                ans[n / 2] -= 3 - sum_digits % 3;
                return ans;
            }
        } else if (k == 7) {
            if (n == 1) return "7";
            if (n == 2) return "77";
            vector<int> rem(n);
            rem[0] = 1;
            for (int i = 1; i < n; ++i) {
                rem[i] = rem[i - 1] * 10 % 7;
            }
            string ans(n, '9');
            int final_rem = 0;
            for (int i = 0; i < n; ++i) {
                final_rem += rem[i] * 9;
                final_rem %= 7;
            }
            int sum_digits = 9 * n - 2;
            if (n % 2 == 1) {
                while (final_rem != 0) {
                    --ans[n / 2];
                    final_rem -= rem[n / 2];
                    final_rem %= 7;
                    final_rem += 7;
                    final_rem %= 7;
                }
            } else {
                while (final_rem != 0) {
                    --ans[n / 2 - 1];
                    --ans[n / 2];
                    final_rem -= rem[n / 2 - 1];
                    final_rem -= rem[n / 2];
                    final_rem %= 7;
                    final_rem += 7;
                    final_rem %= 7;
                }
            }
            return ans;
        }
        return "";
    }
};
