class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s(n, '9');
        if(k==1){
            return s;
        }
        if(k==2){
            s[0] = s.back() = '8';
        }
        if(k==3){
            return s;
        }
        if(k==4){
            s[0] = s.back() = '8';
            if(n>2){
                s[1] = s[n-2] = '8';
            }
        }
        if(k==5){
            s[0] = s.back() = '5';
        }
        if(k==6){
            if(n==1){
                return "6";
            }
            if(n==2){
                return "66";
            }
            if(n%2==1){
                s[0] = s.back() = s[n/2] = '8';
            }
            else{
                s[0] = s.back() = '8';
                s[n/2-1] = s[n/2] = '7';
            }
        }
        if(k==7){

            auto func = [&](string z){
                reverse(z.begin(), z.end());
                int m = 1, sum = 0;
                for(auto x: z){
                    sum += m * (x-'0');
                    sum %= 7;
                    m = (m*10)%7;
                }
                return sum==0;
            };
            if(n%2==0){
                for(int i=9; i>=0; i--){
                    s[n/2-1] = s[n/2] = '0'+i;
                    if(func(s)) return s;
                }
            }
            else{
                for(int i=9; i>=0; i--){
                    s[n/2] = '0'+i;
                    if(func(s)) return s;
                }
            }
        }
        if(k==8){
            s[0] = s[n-1] = '8';
            if(n>2)     s[1] = s[n-2] = '8';
            if(n>4)     s[2] = s[n-3] = '8';
        }
        if(k==9){
            return s;
        }
        return s;
    }
};
