class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s(n,'9');
        if(k==1||k==3||k==9)return s;
        if(k==2){
            s[0]=s[n-1]='8';
            return s;
        }
        if(k==4){
            s[0]=s[n-1]='8';
            if(n>1)s[1]=s[n-2]='8';
            return s;
        }
        if(k==8){
            s[0]=s[n-1]='8';
            if(n>1)s[1]=s[n-2]='8';
            if(n>2)s[2]=s[n-3]='8';
            return s;
        }
        if(k==5){
            s[0]=s[n-1]='5';
            return s;
        }
        if(k==6){
            if(n==1)return "6";
            if(n==2)return "66";
            if(n%2==0)s[0]=s[n-1]='8',s[n/2]=s[n/2-1]='7';
            if(n%2==1)s[0]=s[n-1]='8',s[n/2]='8';
            return s;
        }
        if(k==7){
            for(int i=9;i>=0;i--){
                if(n%2==0)s[n/2]=s[n/2-1]=i+'0';
                if(n%2==1)s[n/2]=i+'0';
                int r=0;
                for(int i=0;i<s.size();i++)r=(r*10+s[i]-'0')%7;
                if(r==0)return s;
            }
            return s;
        }
        return "";
    }
};
