class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return '9' * n
        if k == 2:
            if n == 1:
                return '8'
            if n == 2:
                return '88'
            return ''.join(['8', '9' * (n - 2), '8'])
        if k == 4:
            if n <= 4:
                return '8' * n
            return ''.join(['88', '9' * (n - 4), '88'])
        if k == 5:
            if n <= 2:
                return '5' * n
            return ''.join(['5', '9' * (n - 2), '5'])
        if k == 3:
            return '9' * n
        if k == 6:
            if n <= 2:
                return '6' * n
            if n % 2 == 0:
                return ''.join(['8', '9' * (n // 2 - 2), '77', '9' * (n // 2 - 2), '8'])
            return ''.join(['8', '9' * (n // 2 - 1), '8', '9' * (n // 2 - 1), '8'])
        if k == 8:
            if n <= 6:
                return '8' * n
            return ''.join(['888', '9' * (n - 6), '888'])
        if k == 9:
            return '9' * n
        if n <= 2:
            return '7' * n
        rem = 0
        if ((n - 1) // 2) % 6 > 0:
            rem = int('9' * (((n + 1) // 2 - 1) % 6)) % 7
        rem *= 1 + pow(10, n // 2 + 1, 7)
        rem = rem % 7
        trial = 9 if n % 2 == 1 else 99
        while (rem + trial * pow(10, (n - 1) // 2, 7)) % 7 != 0:
            trial -= 1 if n % 2 == 1 else 11
        return ''.join(['9' * ((n - 1) // 2), str(trial), '9' * ((n - 1) // 2)])
