import sys
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        ans = ""
        sys.set_int_max_str_digits(100005)
        if k == 1:
            ans = "9" * n
        elif k == 2:
            if n == 1:
                ans = "8"
            elif n == 2:
                ans = "88"
            else:
                ans = "8" + "9" * (n - 2) + "8"
        elif k == 3:
            ans = "9" * n
        elif k == 4:
            if n == 1:
                ans = "8"
            elif n == 2:
                ans = "88"
            elif n == 3:
                ans = "888"
            elif n == 4:
                ans = "8888"
            else:
                ans = "88" + "9" * (n - 4) + "88"
        elif k == 5:
            if n == 1:
                ans = "5"
            else:
                ans = "5" + "9" * (n - 2) + "5"
        elif k == 6:
            if n == 1:
                ans = "6"
            elif n == 2:
                ans = "66"
            else:
                ans = "8" + "9" * (n - 2) + "8"
                if n % 2 == 0:
                    ans = ans[:n//2 - 1] + '7' + '7' + ans[n//2 + 1:]
                else:
                    ans = ans[:n//2] + '8' + ans[n//2 + 1:]
        elif k == 7:
            ans = "9" * n
            if n == 1:
                ans = "7"
            elif n == 2:
                ans = "77"
            else:
                if n % 2 == 0:
                    for i in range(9, -1, -1):
                        ans = ans[:n//2 - 1] + str(i) + str(i) + ans[n//2 + 1:]
                        if int(ans) % 7 == 0:
                            break
                else:
                    for i in range(9, -1, -1):
                        ans = ans[:n//2] + str(i) + ans[n//2 + 1:]
                        if int(ans) % 7 == 0:
                            break
        elif k == 8:
            if n == 1:
                ans = "8"
            elif n == 2:
                ans = "88"
            elif n == 3:
                ans = "888"
            elif n == 4:
                ans = "8888"
            elif n == 5:
                ans = "88888"
            elif n == 6:
                ans = "888888"
            else:
                ans = "888" + "9" * (n - 6) + "888"
        elif k == 9:
            ans = "9" * n

        return ans
