class Solution {
public:
    bool check(string& s, int k){
        int r=0;
        for(int i=0;i<s.size();i++){
            r=r*10+s[i]-'0';
            r%=k;
        }return r==0;
    }
    void decrease(string &s){
        int n=s.size();
        if(n%2==1){
            s[n/2]--;
        }else{
            s[n/2]--;s[n/2-1]--;
        }
    }
    string largestPalindrome(int n, int k) {
        string s;
        for(int i=0;i<n;i++)s+='9';
        if(k%2==0){
            s[0]='8';
            s[n-1]='8';
        }if(k%4==0){
            if(n>1)s[1]='8';
            if(n>1)s[n-2]='8';
        }if(k%8==0){
            if(n>2)s[2]='8';
            if(n>2)s[n-3]='8';
        }
        if(k%5==0){
            s[0]='5';
            s[n-1]='5';
        }
        if(k==6 || k==7){
            while(!check(s,k)){
                decrease(s);
            }
        }
        return s;
    }
};
