class Solution {
public:

    string largestPalindrome(int n, int k) {
        string ans="";
        if(k==1 || k==3 || k==9){
            for(int i=0;i<n;i++) ans+='9';
        }
        if(k==2){
            if(n==1) ans="8";
            else{
                ans+='8';
                for(int i=0;i<n-2;i++) ans+='9';
                ans+='8';    
            }

        }
        if(k==4){
            if(n<=4) for(int i=0;i<n;i++) ans+='8';
            else{
                ans+="88";
                for(int i=0;i<n-4;i++) ans+='9';
                ans+="88";
            }
        }
        if(k==5){
            if(n==1) ans="5";
            else{
                ans+='5';
                for(int i=0;i<n-2;i++) ans+='9';
                ans+='5';    
            }

        }
        if( k==8){
            if(n<=6) for(int i=0;i<n;i++) ans+='8';
            else{
                ans+="888";
                for(int i=0;i<n-6;i++) ans+='9';
                ans+="888";
            }
        }
        if( k==6){
            if(n==1) ans="6";
            else if(n==2) ans="66";
            else if(n==3) ans="888";
            else if(n==4) ans="8778";
            else if(n&1){
                ans+='8';
                for(int i=1;i<=(n-3)/2;i++) ans+='9';
                ans+='8';
                for(int i=1;i<=(n-3)/2;i++) ans+='9';
                ans+='8';
            }
            else{


                ans+="8";
                for(int i=1;i<=(n-4)/2;i++) ans+='9';
                ans+="77";
                for(int i=1;i<=(n-4)/2;i++) ans+='9';
                ans+="8";

            }

        }
        if(k==7){
            int t=(n+1)/2;
            int sum=0,now=1;
            for(int i=1;i<t;i++) {
                ans+='9';
                sum=(sum+now*9%7)%7;
                now=now*10%7;
            }
            int tmp=now;
            if(n&1) now=now*10%7;
            else now=now*100%7;
            for(int i=1;i<t;i++){
                sum=(sum+now*9%7)%7;
                now=now*10%7;
            }
            // cout<<sum<<endl;
            for(int j=9;j>=0;j--){
                int t;
                if(n&1) t=j;
                else t=j*10+j;

                if((tmp*t+sum)%7==0) {
                    // cout<<"nd:"<<j<<endl;
                    ans+=(j+'0');
                    if(n%2==0) ans+=(j+'0');
                    break;
                }
            }
            for(int i=1;i<t;i++){
                ans+='9';
            }
        }
        return ans;
    }
};
