class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in [1, 3, 9]:
            return "9" * n

        xs = [9] * n 

        def check7(xs):
            y = 0
            for i in range(3, n+4, 3):
                zs = xs[max(n-i, 0) : n-i+3]
                if i % 2 == 0:
                    base = [100, 10, 1]
                else:
                    base = [-100, -10, -1]
                for b, z in zip(base, zs[::-1]):
                    y += b * z 
            # print(xs, y)
            return y % 7 == 0

        if k == 5: 
            xs[0] = xs[-1] = 5
        elif k == 2:
            xs[0] = xs[-1] = 8
        elif k == 4:
            for i in range(2):
                if i < n:
                    xs[i] = xs[n - 1 - i] = 8
        elif k == 6:
            if n == 1:
                xs = [6]
            elif n == 2:
                xs = [6, 6]
            else:
                xs[0] = xs[-1] = 8
                s = sum(xs)
                if n % 2 == 1:
                    xs[n // 2] -= 1
                else:
                    xs[n//2 - 1] = xs[n//2] = 7                        
        elif k == 7:
            if n < 3:
                xs = [[7], [7,7], [9,5,9]][n-1]
            if n % 2 == 1:
                while not check7(xs):
                    xs[n//2] -= 1
            else:
                while not check7(xs):
                    xs[n//2-1] -=1
                    xs[n//2] -= 1

        elif k == 8:
            for i in range(3):
                if i < n:
                    xs[i] = xs[n - 1 - i] = 8

        return "".join([str(x) for x in xs])
