class Solution {
public:
    string largestPalindrome(int n, int k) 
    {
        string ans(n,'9');
        if(k==2)
        {
            ans[0]=ans[n-1]='8';
        }
        else if(k==4)
        {
            if(n==1)
            {
                return "8";
            }
            ans[0]=ans[n-1]='8';
            ans[1]=ans[n-2]='8';
        }
        else if(k==5)
        {
            ans[0]=ans[n-1]='5';
        }
        else if(k==6)
        {
            if(n==1)
            {
                return "6";
            }
            if(n==2)
            {
                return "66";
            }
            ans[0]=ans[n-1]='8';
            if(n&1)
            {
                ans[n/2]='8';
            }
            else
            {
                ans[n/2]=ans[n/2-1]='7';
            }
        }
        else if(k==7)
        {
            int rem=0;
            if(n&1)
            {
                int a=0;
                for(int i=0,curr=1;i<n;i++,curr=(curr*10)%7)
                {
                    if(i==n/2)
                    {
                        a=curr;
                        continue;
                    }
                    rem=(rem+9*curr)%7;
                }
                for(int i=3;i<=9;i++)
                {
                    if((rem+a*i)%7==0)
                    {
                        ans[n/2]=(char)('0'+i);
                        break;
                    }
                }
            }
            else
            {
                int a,b;
                for(int i=0,curr=1;i<n;i++,curr=(curr*10)%7)
                {
                    if(i==n/2||i==n/2-1)
                    {
                        if(i==n/2)
                        {
                            a=curr;
                        }
                        else
                        {
                            b=curr;
                        }
                        continue;
                    }
                    rem=(rem+9*curr)%7;
                }
                for(int i=3;i<=9;i++)
                {
                    if((rem+(a+b)*i)%7==0)
                    {
                        ans[n/2]=ans[n/2-1]=(char)('0'+i);
                    }
                }
            }
        }
        else if(k==8)
        {
            if(n==1)
            {
                return "8";
            }
            else if(n==2)
            {
                return "88";
            }
            ans[0]=ans[n-1]='8';
            ans[1]=ans[n-2]='8';
            ans[2]=ans[n-3]='8';
        }
        return ans;
    }
};
