class Solution {
    public String largestPalindrome(int n, int k) {

        // 打表找规律
        if (k == 1) {
            return "9".repeat(n);
        } else if (k == 2) {
            if (n == 1) return "8";
            else {
                return "8" + ("9".repeat(n - 2)) + "8";
            }
        } else if (k == 3) {
            return "9".repeat(n);
        } else if (k == 4) {
            if (n <= 4) {
                return "8".repeat(n);
            } else {
                return "88" + ("9".repeat(n - 4)) + "88";
            }
        } else if (k == 5) {
            if (n <= 2) return "5".repeat(n);
            else return "5" + ("9".repeat(n - 2)) + "5";
        } else if (k == 6) {
            String[] str = {"6", "66", "888", "8778", "89898", "897798"};
            if (n <= str.length) {
                return str[n - 1];
            }


            if (n <= 2) return "6".repeat(n);

            StringBuilder sb = new StringBuilder();
            if (n % 2 == 0) {
                int acc = 0;
                sb.append("8");
                acc += 8;
                for (int i = 1; i < n / 2 - 1; i++) {
                    sb.append("9");
                    acc += 9;
                }
                if (acc % 3 == 0) {
                    sb.append("99");
                } else if (acc % 3 == 1) {
                    sb.append("88");
                } else {
                    sb.append("77");
                }

                for (int i = 1; i < n / 2 - 1; i++) {
                    sb.append("9");
                }
                sb.append("8");
                return sb.toString();
            } else {

                int acc = 0;
                sb.append("8");
                acc += 8;
                for (int i = 1; i < n / 2 - 1; i++) {
                    sb.append("9");
                    acc += 9;
                }
                if (acc % 3 == 0) {
                    sb.append("999");
                } else if (acc % 3 == 1) {
                    sb.append("979");
                } else {
                    sb.append("989");
                }

                for (int i = 1; i < n / 2 - 1; i++) {
                    sb.append("9");
                }
                sb.append("8");
                return sb.toString();

            }


        } else if (k == 7) {

            String[] str = {"7", "77", "959", "9779", "99799", "999999", "9994999", "99944999"};
            if (n <= str.length) {
                return str[n - 1];
            } 

            return sep(n);
