            // }
            // return ("9".repeat(n / 2 - 1)) + "44" + ("9".repeat(n / 2 - 1));
        } else if (k == 8) {
            if (n <= 6) return "8".repeat(n);
            return "888" + ("9".repeat(n - 6)) + "888";
        } else {
            return "9".repeat(n);
        }

    }




        String sep(int n) {

            int k = 7;
            StringBuilder sb = new StringBuilder();
            int[] rs = new int[n];
            rs[0] = 1;
            for (int i = 1; i < n; i++) {
                rs[i] = rs[i - 1] * 10 % k;
            }

            int acc = 0;

            if (n % 2 == 0) {
                for (int i = 0, j = n - 1; i < n / 2 - 1; i++, j--) {
                    acc += 9 * rs[i] % k;
                    acc += 9 * rs[j] % k;
                    acc %= 7;
                }
                for (int i = 9; i >= 0; i--) {
                    int x = i * (rs[n / 2 - 1] + rs[n / 2]) % k;
                    if ((acc + x) % k == 0) {
                        return ("9".repeat(n / 2 - 1)) + ("" + i).repeat(2) + ("9".repeat(n / 2 - 1));
                    }
                }
            } else {
                for (int i = 0, j = n - 1; i < n / 2; i++, j--) {
                    acc += 9 * rs[i] % k;
                    acc += 9 * rs[j] % k;
                    acc %= 7;
                }
                for (int i = 9; i >= 0; i--) {
                    int x = i * rs[n / 2] % k;
                    if ((acc + x) % k == 0) {
                        return ("9".repeat(n / 2)) + ("" + i) + ("9".repeat(n / 2));
                    }
                }
            }

            return "";
        }

}
