class Solution {
public:
    string largestPalindrome(int n, int k) {
        string res(n, '9');
        int i;
        switch(k){
            case 1:
                for(i=0;i<n;i++){
                    res[i]='9';
                }
                break;
            case 2:
                for(i=1;i<n-1;i++){
                    res[i]='9';
                }
                res[0]='8';
                res[n-1]='8';
                break;
            case 3:
                for(i=0;i<n;i++){
                    res[i]='9';
                }
                break;
                break;
            case 4:
                for(i=2;i<n-2;i++){
                    res[i]='9';
                }
                res[0]='8';
                res[n-1]='8';
                if(n>1){
                    res[1]='8';
                    res[n-2]='8';
                }
                break;

            case 5:
                for(i=1;i<n-1;i++){
                    res[i]='9';
                }
                res[0]='5';
                res[n-1]='5';
                break;

            case 6:
                if(n>=3){
                    for(i=1;i<n-1;i++){
                        res[i]='9';
                    }
                    res[0]='8';
                    res[n-1]='8';
                    if(n%2){
                        res[n/2]='8';
                    } else {
                    res[n/2]='7';
                    res[(n-1)/2]='7';
                    }
                } else {
                    for(i=0;i<n;i++){
                        res[i]='6';
                    }
                }
                break;
            case 7:
                for(i=0;i<n;i++){
                    res[i]='9';
                }
                switch(n%12){
                    case 1:
                        res[n/2]='7';
                        break;
                    case 2:
                        res[n/2]='7';
                        res[n/2-1]='7';
                        break;
                    case 3:
                        res[n/2]='5';
                        break;
                    case 4:
                        res[n/2]='7';
                        res[n/2-1]='7';
                        break;

                    case 5:
                        res[n/2]='7';
                        break;

                    case 6:
                        res[n/2]='9';
                        res[n/2-1]='9';
                        break;
                    case 7:
                        res[n/2]='4';
                        break;
                    case 8:
                        res[n/2]='4';
                        res[n/2-1]='4';
                        break;
                    case 9:
                        res[n/2]='6';
                        break;
                    case 10:
                        res[n/2]='4';
                        res[n/2-1]='4';
                        break;
                    case 11:
                        res[n/2]='4';
                        break;
                }
                break;
            case 8:
                for(i=0;i<n;i++){
                    res[i]='9';
                }
                res[0]='8';
                res[n-1]='8';
                if(n>1){
                    res[1]='8';
                    res[n-2]='8';
                }
                if(n>2){
                    res[2]='8';
                    res[n-3]='8';
                }
                break;
            case 9:
                for(i=0;i<n;i++){
                    res[i]='9';
                }
                break;
                break;

        }
        return res;
    }
};
