class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def check7(num, n):
            c1, c2 = 0, 0
            c1 += int(num[:n % 3]) if n % 3 else 0
            for i in range(n // 3):
                if i % 2:
                    c1 += int(num[n % 3 + i * 3:n % 3 + (i + 1) * 3])
                else:
                    c2 += int(num[n % 3 + i * 3:n % 3 + (i + 1) * 3])
            return abs(c1 - c2) % 7 == 0

        if k == 1 or k == 3 or k == 9:
            return "9" * n
        elif k == 2:
            return "8" + "9" * (n - 2) + "8" if n > 1 else "8"
        elif k == 4:
            if n <= 4:
                return "8" * n
            return "88" + "9" * (n - 4) + "88"
        elif k == 8:
            if n <= 6:
                return "8" * n
            return "888" + "9" * (n - 6) + "888"
        elif k == 5:
            return "5" + "9" * (n - 2) + "5" if n > 1 else "5"
        elif k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n % 2:
                return "8" + "9" * (n // 2 - 1) + "8" + "9" * (n // 2 - 1) + "8"
            else:
                return "8" + "9" * (n // 2 - 2) + "77" + "9" * (n // 2 - 2) + "8"
        elif k == 7:
            if n <= 2:
                return "7" * n
            if n % 2:
                for i in range(9, -1, -1):
                    num = "9" * (n // 2) + str(i) + "9" * (n // 2)
                    if check7(num, n):
                        return num
            else:
                for i in range(9, -1, -1):
                    num = "9" * (n // 2 - 1) + str(i) * 2 + "9" * (n // 2 - 1)
                    if check7(num, n):
                        return num
