class Solution {
public:
    bool check(string &s, int n){
        int sum[2] = {}, cur = 0;
        for(int i=0;i<n;i+=3){
            int res = s[n-i-1]-'0';
            if(i+1<n) res += (s[n-i-2]-'0')*10;
            if(i+2<n) res += (s[n-i-3]-'0')*100;
            sum[cur] += res;
            cur ^= 1;
        }
        int diff = abs(sum[0]-sum[1]);
        if(diff%7==0) return true;
        return false;
    }
    string largestPalindrome(int n, int k) {
        string res = "";
        if(k==1 || k==3 || k==9){
            for(int i=0;i<n;i++) res += '9';
            return res;
        }

        if(k==2){
            if(n==1) return "8";
            res += '8';
            for(int i=0;i<n-2;i++) res += '9';
            res += '8';
            return res;
        }

        if(k==4){
            if(n<=4){
                for(int i=0;i<n;i++) res += '8';
                return res;
            }
            res += "88";
            for(int i=0;i<n-4;i++) res += '9';
            res += "88";
            return res;
        }

        if(k==8){
            if(n<=6){
                for(int i=0;i<n;i++) res += '8';
                return res;
            }
            res += "888";
            for(int i=0;i<n-6;i++) res += '9';
            res += "888";
            return res;
        }

        if(k==5){
            if(n==1) return "5";
            res += '5';
            for(int i=0;i<n-2;i++) res += '9';
            res += '5';
            return res;
        }

        if(k==6){
            if(n==1) return "6";
            if(n==2) return "66";
            res += '8';
            for(int i=0;i<n-2;i++) res += '9';
            res += '8';
            int rem = ((n-2)*9+8*2)%3;
            if(n&1){
                res[n/2] -= rem;
                return res;
            }
            if(rem==1) res[n/2-1] = res[n/2] = '7';
            else if(rem==2) res[n/2-1] = res[n/2] = '8';
            return res;
        }

        for(int i=0;i<n;i++) res += '9';
        if(check(res,n)) return res;
        for(int i=8;i>=0;i--){
            if(n&1) res[n/2] = (i+'0');
            else res[n/2-1] = res[n/2] = (i+'0');
            if(check(res,n)) return res;
        }
        return res;
    }
};
