class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        res = [9 for i in range(n)]

        if k == 1:
            pass
        elif k == 2:
            res[-1] = 8
            res[0] = 8
        elif k == 3:
            pass
        elif k == 4:
            if n == 1:
                res = [8]
            elif n == 2:
                res = [8, 8]
            else:
                res[-1] = 8
                res[-2] = 8
                res[0] = 8
                res[1] = 8
        elif k == 5:
            res[-1] = 5
            res[0] = 5
        elif k == 6:
            if n == 1:
                res = [6]
            elif n == 2:
                res = [6, 6]
            else:
                res[-1] = 8
                res[0] = 8
                if n % 2 == 1:
                    res[n//2] = 8
                else:
                    res[n//2] = 7
                    res[n//2-1] = 7
        elif k == 7:
            d2m = [1, 3, 2, 6, 4, 5]
            cur = 0
            for i in range(n):
                cur += 9 * d2m[i % 6]
            cur %= 7
            if n % 2 == 1: # only change middle
                m = n // 2
                while cur != 0:
                    res[m] -= 1
                    loss = d2m[m % 6]
                    cur = (cur + 7 - loss) % 7
            else:
                m1 = n // 2
                m2 = m1 - 1
                while cur != 0:
                    res[m1] -= 1
                    res[m2] -= 1
                    loss = d2m[m1 % 6] + d2m[m2 % 6]
                    cur = (cur + 7 - loss) % 7
        elif k == 8:
            if n <= 5:
                res = [8] * n
            else:
                res[-1] = 8
                res[-2] = 8
                res[-3] = 8
                res[0] = 8
                res[1] = 8
                res[2] = 8
        elif k == 9:
            pass
        return ''.join(map(str, res))
