class Solution {
public:
    // string ans;
    bool found;
    void solve(vector<int> &rem,int s, int i, int j, int r, int k, string & ans, vector<vector<int>> &dp){
        if(i > j){
            if(r== 0)
                found = true;
            return;
        }
        if(dp[i][r] != -1)
            return;
        for(int a=9; a>=s && !found; a--){
            ans[i] = '0' + a;
            ans[j] = '0' + a;
            int rt = r;
            rt = (rt + rem[i]*a)%k;
            if(i != j) rt = (rt + rem[j]*a)%k;
            solve(rem, 0, i+1, j-1, rt, k, ans, dp);
        }
        dp[i][r] = false;
    }
    string largestPalindrome(int n, int k) {
        vector<int> rem(n);
        vector<vector<int>> dp(n, vector<int>(k, -1));
        rem[0] = 1;
        string ans(n, '0');
        for(int i=1; i<n;i++){
            rem[i] = (rem[i-1]*10)%k;
            // cout<<rem[i];
        }
        solve(rem, 1, 0, n-1, 0, k, ans, dp);
        return ans;
    }
};
