class Solution {
public:
    string seven(int n) {
        if (n == 0) return "";
        if (n == 1) return "7";
        if (n == 2) return "77";
        if (n == 3) return "959";
        if (n == 4) return "9779";
        if (n == 5) return "99799";
        if (n == 6) return "999999";
        if (n == 7) return "9994999";
        if (n == 8) return "99944999";
        if (n == 9) return "999969999";
        if (n == 10) return "9999449999";
        if (n == 11) return "99999499999";
        string res = "";
        for (int i = 0; i < n/12; i++) {
            res += "999999";
        }
        res += seven(n % 12);
        for (int i = 0; i < n/12; i++) {
            res += "999999";
        }
        return res;
    }
    string largestPalindrome(int n, int k) {
        string res = "";
        if (k == 1 || k == 3 || k == 9) {
            for (int i = 0; i < n; i++) res += '9';
            return res;
        }
        if (n == 1) {
            if (k >= 5) return to_string(k);
            return "8";
        }
        if (k == 5) {
            res = "5";
            for (int i = 1; i < n - 1; i++) res += '9';
            res += '5';
            return res;
        }
        if (k == 2) {
            res = "8";
            for (int i = 1; i < n - 1; i++) res += '9';
            res += "8";
            return res;
        }
        if (k == 4) {
            if (n == 2) return "88";
            else if (n == 3) return "888";
            res = "88";
            for (int i = 2; i < n - 2; i++) res += '9';
            res += "88";
            return res;
        }
        if (k == 8) {
            if (n <= 5) {
                for (int i = 0; i < n; i++) res += '8';
                return res;
            }
            res = "888";
            for (int i = 3; i < n - 3; i++) res += '9';
            res += "888";
            return res;
        }
        if (k == 6) {
            if (n == 2) return "66";
            if (n % 2 == 1) {
                res = "8";
                for (int i = 1; i < n/2; i++) res += '9';
                res += "8";
                for (int i = 1; i < n/2; i++) res += '9';
                res += "8";
                return res;
            }
            res = "8";
            for (int i = 1; i < n/2 - 1; i++) res += '9';
            res += "77";
            for (int i = 1; i < n/2 - 1; i++) res += '9';
            res += "8";
            return res;
        }
        return seven(n);
    }
};
