class Solution {
public:
    string largestPalindrome(int n, int k) {
        vector<int> v(n, 9), p(n);
        if(n > 2 && !(k % 5)) v[0] = v[n - 1] = 5;
        if(n > 6 && !(k % 8)) v[2] = v[n - 3] = 8;
        if(n > 4 && !(k % 4)) v[1] = v[n - 2] = 8;
        if(n > 2 && !(k % 2)) v[0] = v[n - 1] = 8;

        int md = 0;
        for(int i = 0, j = 1; i < n; i++, (j *= 10) %= k){
            p[i] = j;
            (md += p[i] * v[i]) %= k;
        } 

        while(md){
            for(int i = 0; i < 2 - (n & 1); i++){
                int x = n / 2 - i;
                v[x]--;
                (md += k - p[x]) %= k;
            }
            if(!v[n / 2]) break;
        }

        string s;
        for(int i = n - 1; ~i; i--) s += to_string(v[i]);
        return s;
    }
};
