class Solution {
public:


    int bexp(int n,int k,int mod){

        if(k==0){
            return 1;
        }
        n%=mod;
        if(k&1){
            return ((n%mod)*(bexp(n,k-1,mod)%mod))%mod;
        }

        return bexp(((n%mod)*(n%mod))%mod,k/2,mod);
    }
    string largestPalindrome(int n, int k) {

        int mx=0;

        if(n<=6){
            int l=pow(10,n);
            for(int i=1;i<=l;++i){

                if(i%k){
                    continue;
                }
                string s1=to_string(i);
                string s2=s1;
                reverse(s2.begin(),s2.end());
                if(s1==s2){
                    mx=max(mx,i);
                }

            }

            return to_string(mx);
        }






        string ans="";
        if(k==1 or k==3 or k==9){
            for(int i=0;i<n;++i){
                ans+="9";
            }
            return ans;
        }

        if(k==2){
            for(int i=0;i<n;++i){
                if(i==0 or i==n-1){
                    ans+="8";
                }
                else{
                    ans+="9";
                }
            }
            return ans;
        }

        if(k==4){
            string now1="",now2="";
            now1+="88";
            now2+="88";
            for(int i=0;i<(n-4)/2;++i){
                now1+="9";
                now2+="9";
            }
            if(n&1){
                now1+="9";
            }
            reverse(now2.begin(),now2.end());
            return now1+now2;
        }


        if(k==8){
            string now1="",now2="";
            now1+="888";
            now2+="888";
            for(int i=0;i<(n-6)/2;++i){
                now1+="9";
                now2+="9";
            }
            if(n&1){
                now1+="9";
            }
            reverse(now2.begin(),now2.end());
            return now1+now2;
        }

        if(k==5){
            string now1="",now2="";
            now1+="5";
            now2+="5";
            for(int i=0;i<(n-2)/2;++i){
                now1+="9";
                now2+="9";
            }
            if(n&1){
                now1+="9";
            }
            reverse(now2.begin(),now2.end());
            return now1+now2;
        }

        if(k==6){
            vector<int> an(n,0);
            an[0]=8;
            an[n-1]=8;
            int sm=16;
            if(n&1){
                for(int i=1;i<n-1;++i){
                    if(i!=(n/2)){
                        an[i]=9;
                        sm+=an[i];
                    }
                }
                for(int x=9;x>=0;--x){
                    if((sm+x)%6==0){
                        an[(n)/2]=x;
                        break;
                    }
                }
            }
            else{
                int l=n/2-1,r=n/2;
                for(int i=1;i<n-1;++i){
                    if(i!=l and i!=r){
                        an[i]=9;
                        sm+=an[i];
                    }
                }

                for(int x=9;x>=0;--x){
                    if((sm+x+x)%6==0){
                        an[l]=x;
                        an[r]=x;
                        break;
                    }
                }
            }

            for(auto i:an){
                ans+=(char)(i+'0');
            }
            return ans;
        }


        if(k==7){

            vector<int> an(n,0);

            int sm=0;

            if(n&1){
                for(int i=0;i<n;++i){
                    if(i!=(n/2)){
                        int pw=n-1-i;
                        int v=bexp(10,pw,7);
                        v*=9;
                        v%=7;
                        an[i]=9;
                        sm+=v;
                        sm%=7;
                    }
                }
                for(int x=9;x>=0;--x){


                    int pw=n-1-(n/2);
                    int v=bexp(10,pw,7);
                    v*=x;
                    v%=7;
                    if((sm+v)%7==0){
                        an[(n)/2]=x;
                        break;
                    }
                }
            }
            else{
                int l=n/2-1,r=n/2;
                for(int i=0;i<n;++i){
                    if(i!=l and i!=r){
                        int pw=n-1-i;
                        int v=bexp(10,pw,7);
                        v*=9;
                        v%=7;
                        an[i]=9;
                        sm+=v;
                        sm%=7;
                    }
                }

                for(int x=9;x>=0;--x){

                    int pw1=n-1-(n/2);
                    int pw2=n-1-(n/2-1);
                    int v1=bexp(10,pw1,7);
                    int v2=bexp(10,pw2,7);
                    v1*=x;
                    v2*=x;
                    v1+=v2;
                    v1%=7;
                    if((sm+v1)%7==0){
                        an[n/2]=x;
                        an[n/2-1]=x;
                        break;
                    }
                }
            }

            for(auto i:an){
                ans+=(char)(i+'0');
            }
            return ans;

        }

        return "d";


    }
};
