class Solution {
public:
    int pangkat(int a, int b, int mod) {
        if(b == 0) return 1;
        return (b&1 ? a : 1) * pangkat(a*a%mod, b>>1, mod) % mod;
    }
    string largestPalindrome(int n, int k) {
        string ret(n, '9');
        if(k == 1 || k == 3 || k == 9) {
            return ret;
        } else if(k == 2 || k == 4 || k == 8) {
            ret[0] = ret.back() = '8';
            if(k >= 4 && n > 2) ret[1] = ret[n-2] = '8';
            if(k == 8 && n > 4) ret[2] = ret[n-3] = '8';
            return ret;
        } else if(k == 5) {
            ret[0] = ret.back() = '5';
            return ret;
        } else if(k == 6) {
            if(n == 1) return "6";
            if(n == 2) return "66";
            ret[0] = ret.back() = '8';
            if(n&1) ret[n/2] = '8';
            else ret[n/2] = ret[n/2-1] = '7';
            return ret;
        } else if(k == 7) {
            if(n == 1) return "7";
            if(n == 2) return "77";
            for(int i = 0; i < 7; i++) {
                int sisa = (pangkat(10%7, n, 7) - 1 + 7) % 7;
                if(n&1) sisa -= i * (pangkat(10%7, n/2, 7)) % 7;
                else sisa -= i * ((pangkat(10%7, n/2, 7) + pangkat(10%7, n/2-1, 7)) % 7) % 7;
                sisa += 7;
                sisa %= 7;
                if(sisa == 0) {
                    if(n&1) ret[n/2] -= i;
                    else ret[n/2] = ret[n/2-1] -= i;
                    return ret;
                }
            }
        }
        return ret;
    }
};
