class Solution {
public:
    int mx(int k) {
        vector<int> r{0, 9, 8, 9, 8, 5, 8, 9, 8, 9};
        return r[k];
    }

    bool can(vector<int>& v, int k) {
        // for (int i:v) cout <<i;
        // cout << endl;
        int r=0;
        for (int i: v) {
            r=r*10+i;
            r=r%k;
        }
        return r==0;
    }

    string largestPalindrome(int n, int k) {
        vector<int> v(n);
        if (k==8) {
            v=vector<int>(n, 9);
            v[0]=8;
            v[n-1]=8;
            if (n>2) {
                v[1]=8;
                v[n-2]=8;
            }
            if (n>4) {
                v[2]=8;
                v[n-3]=8;
            }
            string s="";
            for (int i: v) {
                s+='0'+i;
            }
            return s;
        }
        if (k==4) {
            v=vector<int>(n, 9);
            v[0]=8;
            v[n-1]=8;
            if (n>2) {
                v[1]=8;
                v[n-2]=8;
            }
            string s="";
            for (int i: v) {
                s+='0'+i;
            }
            return s;
        }
        v[0]=mx(k);
        v[n-1]=mx(k);
        for (int i=1; i<n-1; ++i) {
            v[i]=9;
        }
        int off=0;
        while (true) {
            if (n/2<off) break;
            vector<int> v2(v.begin(), v.end());
            for (int d=9; d>=0; --d) {
                if (n%2==0) {
                    v2[n/2 + off]=d;
                    v2[n/2-1 - off]=d;
                } else {
                    v2[n/2 + off]=d;
                    v2[n/2 - off]=d;
                }
                if (can(v2, k)) {
                    string s="";
                    for (int i: v2) {
                        s+='0'+i;
                    }
                    return s;
                }
            }
            off ++;
        }
        return "";
    }
};
