class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        if k == 2:
            if n == 1:
                return "8"
            return "8" + ("9" * (n - 2)) + "8"
        if k == 4:
            if n <= 4:
                return "8" * n
            return "88" + ("9" * (n - 4)) + "88"
        if k == 5:
            if n <= 2:
                return "5" * n
            return "5" + ("9" * (n - 2)) + "5"
        if k == 6:
            if n == 1:
                return "6"
            if n == 2:
                return "66"
            if n == 3:
                return "888"
            if n == 4:
                return "8778"
            if n % 2 == 1:
                return "8" + ("9" * ((n - 3) // 2)) + "8" + ("9" * ((n - 3) // 2)) + "8"
            return "8" + ("9" * ((n - 4) // 2)) + "77" + ("9" * ((n - 4) // 2)) + "8"
        if k == 7:
            num9 = (n - 1) // 2 if n % 2 == 1 else (n - 2) // 2
            s9 = "9" * num9
            if n % 12 == 1:
                return s9 + "7" + s9
            if n % 12 == 2:
                return s9 + "77" + s9
            if n % 12 == 3:
                return s9 + "5" + s9
            if n % 12 == 4:
                return s9 + "77" + s9
            if n % 12 == 5:
                return s9 + "7" + s9
            if n % 12 == 6:
                return s9 + "99" + s9
            if n % 12 == 7:
                return s9 + "4" + s9
            if n % 12 == 8:
                return s9 + "44" + s9
            if n % 12 == 9:
                return s9 + "6" + s9
            if n % 12 == 10:
                return s9 + "44" + s9
            if n % 12 == 11:
                return s9 + "4" + s9
            return s9 + "99" + s9
        if k == 8:
            if n <= 6:
                return "8" * n
            return "888" + ("9" * (n - 6)) + "888"
