class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9)
        {
            string str;
            for(int i = 0; i<n; i++)
            {
                str.push_back('9');
            }
            return str;
        }
        else if(k == 2)
        {
            if(n == 1)
            {
                return "8";
            }

            string str;
            str.push_back('8');
            for(int i = 0; i<n-2; i++)
            {
                str.push_back('9');
            }
            str.push_back('8');
            return str;
        }
        else if(k == 4)
        {
            if(n == 1)
            {
                return "8";
            }
            if(n == 2)
            {
                return "88";    
            }
            else if(n == 3)
            {
                return "888";
            }
            string str;
            str.push_back('8');
            str.push_back('8');
            for(int i = 0; i<n-4; i++)
            {
                str.push_back('9');
            }
            str.push_back('8');
            str.push_back('8');
            return str;
        }
        else if(k == 5)
        {
            if(n == 1)
            {
                return "5";
            }
            string str;
            str.push_back('5');
            for(int i = 0; i<n-2; i++)
            {
                str.push_back('9');
            }
            str.push_back('5');
            return str;
        }
        else if(k == 6)
        {
            if(n == 1)
            {
                return "6";
            }
            if(n == 2)
            {
                return "66";
            }

            int n1 = n/2;
            n1--;
            if(n%2 == 0)
            {
                n1--;
            }
            string str;
            str.push_back('8');

            for(int i = 0; i<n1; i++)
            {
                str.push_back('9');
            }
            if(n%2 == 0)
            {
                str.push_back('7');
                str.push_back('7');
            }
            else
            {
                str.push_back('8');
            }
            for(int i = 0; i<n1; i++)
            {
                str.push_back('9');
            }
            str.push_back('8');
            return str;

        }
        else if(k == 7)
        {
            int n1 = n%12;
            // cout<<n1<<endl;
            if(n%2 == 0)
            {
                string str;
                for(int i = 0; i<(n/2)-1; i++)
                {
                    str.push_back('9');
                }
                if(n1 == 4 || n1 == 2)
                {
                    str.push_back('7');
                    str.push_back('7');
                }
                else if(n1 == 6 || n1 == 0)
                {
                    str.push_back('9');
                    str.push_back('9');
                }
                else if(n1 == 8 || n1 == 10)
                {
                    str.push_back('4');
                    str.push_back('4');
                }
                for(int i = 0; i<(n/2)-1; i++)
                {
                    str.push_back('9');
                }
                return str;
            }
            else
            {
                string str;
                for(int i = 0; i<n/2; i++)
                {
                    str.push_back('9');
                }
                if(n1 == 1 || n1 == 5)
                {
                    str.push_back('7');
                }
                else if(n1 == 3)
                {
                    str.push_back('5');
                }
                else if(n1 == 7 || n1 == 11)
                {
                    str.push_back('4');
                }
                else if(n1 == 9)
                {
                    str.push_back('6');
                }
                for(int i = 0; i<n/2; i++)
                {
                    str.push_back('9');
                }
                return str;
            }
        }
        else if(k == 8)
        {
            if(n == 1)
            {
                return "8";
            }
            if(n == 2)
            {
                return "88";    
            }
            else if(n == 3)
            {
                return "888";
            }
            else if(n == 4)
            {
                return "8888";
            }
            else if(n == 5)
            {
                return "88888";
            }
            string str;
            str.push_back('8');
            str.push_back('8');
            str.push_back('8');
            for(int i = 0; i<n-6; i++)
            {
                str.push_back('9');
            }
            str.push_back('8');
            str.push_back('8');
            str.push_back('8');
            return str;
        }
        return "";
    }
};
