class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        ans = [""] * n
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        elif k == 2:
            ans[0] = "8"
            ans[n - 1] = "8"
        elif k == 4:
            if n == 1:
                return "8"
            else:
                ans[0] = "8"
                ans[1] = "8"
                ans[-1] = "8"
                ans[-2] = "8"
        elif k == 5:
            ans[0] = "5"
            ans[-1] = "5"
        elif k == 6:
            if n <= 4:
                for i in range(int("9" * n), 0, -1):
                    if i % 6 == 0:
                        s = str(i)
                        if s == s[::-1]:
                            return s
            else:
                for i in range(n):
                    ans[i] = "9"
                ans[0] = "8"
                ans[-1] = "8"
                if n % 2 == 1:
                    ans[n // 2] = "8"
                else:
                    ans[n // 2 - 1] = "7"
                    ans[n // 2] = "7"
        elif k == 7:
            if n <= 4:
                for i in range(int("9" * n), 0, -1):
                    if i % 7 == 0:
                        s = str(i)
                        if s == s[::-1]:
                            return s
            else:
                yu = 0
                for i in range(n):
                    ans[i] = "9"
                    yu = (yu * 10 + 2) % 7
                if yu != 0:
                    if n % 2 == 1:
                        i = 9
                        yu2 = 0
                        while yu2 != yu:
                            i -= 1
                            yu2 = ((9 - i) * pow(10, (n - 1) // 2, 7)) % 7
                        ans[n // 2] = str(i)
                    else:
                        i = 9
                        yu2 = 0
                        while yu2 != yu:
                            i -= 1
                            yu2 = ((9 - i) * pow(10, (n - 1) // 2, 7) + (9 - i) * pow(10, n // 2, 7)) % 7
                        ans[n // 2] = str(i)
                        ans[n // 2 - 1] = str(i)
                # print(ans)
                # exit()
        elif k == 8:
            if n <= 6:
                for i in range(int("9" * n), 0, -1):
                    if i % 8 == 0:
                        s = str(i)
                        if s == s[::-1]:
                            return s
            ans[0] = "8"
            ans[1] = "8"
            ans[2] = "8"
            ans[-1] = "8"
            ans[-2] = "8"
            ans[-3] = "8"

        i = 0
        j = n - 1
        while i <= j:
            if ans[i] == "":
                ans[i] = "9"
                ans[j] = "9"
            i += 1
            j -= 1
        return "".join(ans)
