class Solution {
public:
    int remm(int n){
        if(n % 2 == 0){
            int nn = 2;
            int curr = 2;
            int curr2 = 1;

            int r = 0;
            for(int i = 1; i < n; i++){
                curr *= 10;
                curr %= 7;
                curr2 *= 10;
                curr2 %= 7;
                if(i != n/2 && i != n/2-1){
                    nn += curr;
                    nn %= 7;
                }
                else{
                    r += curr2;
                    r %= 7;
                }
                //cout << i << ' ' << nn << "x\n";
            }
            //cout << nn << ' ' << r << '\n';
            for(int i = 9; i >= 0; i--){
                if((r*i + nn) % 7 == 0)
                    return i;
            }

        }
        int nn = 2;
        int curr = 2;
        int r = 0;
        int curr2 = 1;
        for(int i = 1; i < n; i++){
            curr *= 10;
            curr %= 7;
            curr2 *= 10;
            curr2 %= 7;
            if(i != n/2){
                nn += curr;
                nn %= 7;
            }
            else{
                r += curr2;
                r %= 7;
            }
        }
        //cout << nn << ' ' << r << '\n';
        for(int i = 9; i >= 0; i--){
            if((r*i + nn) % 7 == 0)
                return i;
        }
        return 7;
    }
    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9){
            string s(n, '9');
            return s;
        }
        if(k == 2){
            string s = "8";
            for(int i = 1; i < n-1; i++)
                s += '9';
            if(n > 1)
                s += '8';
            return s;
        }
        if(k == 4){
            if(n == 1)
                return "8";
            if(n == 2)
                return "88";
            string s(n, '8');
            for(int i = 2; i < (n+1)/2; i++){
                s[i] = '9';
                s[n-1-i] = '9';
            }
            return s;

        }
        if(k == 5){
            if(n == 1){
                return "5";
            }
            string s(n, '5');
            for(int i = 1; i < (n+1)/2; i++){
                s[i] = '9';
                s[n-1-i] = '9';
            }
            return s;
        }
        if(k == 6){
            if(n == 1){
                return "6";
            }
            if(n == 2){
                return "66";
            }
            string s(n, '9');
            s[0] = '8';
            s[n-1] = '8';
            if(n % 2 == 0){
                s[n/2 - 1] = '7';
                s[n/2] = '7';
            }
            else{
                s[n/2] = '8';
            }
            return s;
        }
        if(k == 8){
            string s(n, '8');
            for(int i = 3; i < (n+1)/2; i++){
                s[i] = '9';
                s[n-1-i] = '9';
            }
            return s;
        }
        if(k == 7){
            if(n == 1)
                return "7";
            if(n == 2)
                return "77";
            string s(n, '9');
            char x = '0' + remm(n);
            s[n/2] = x;
            if(n%2 == 0){
                s[n/2-1] = x;

            }
            return s;

        }
        return "0";
    }
};
