class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n == 1:
            for x in range(9, 0, -1):
                if x % k == 0:
                    return str(x)
        if n <= 6:
            for x in range(int("9" * n), 0, -1):
                s = str(x)
                if x % k == 0 and s == s[::-1]:
                    return s
        if k in {1, 3, 9}:
            return "9" * n
        if k == 2:
            return "8" + "9" * (n - 2) + "8"
        if k == 5:
            return "5" + "9" * (n - 2) + "5"
        if k == 4:
            return "88" + "9" * (n - 4) + "88"
        if k == 8:
            return "888" + "9" * (n - 6) + "888"

        def div7(s):
            x = 0
            for c in s:
                x = ((x * 3) + int(c)) % 7
            return x == 0

        h = (n + 1) // 2
        if k == 6:
            for j in range(10):
                left = "8" + "9" * (h - 2) + str(9 - j)
                v = 8 + 9 * (len(left) - 1) - j
                if n % 2 == 0:
                    if v % 3 == 0:
                        return left + left[::-1]
                else:
                    if (v * 2 - (9 - j)) % 3 == 0:
                        return left + left[:-1][::-1]
        else:
            if k == 7:
                left0 = "9" * (h - 1)
            for j in range(9, -1, -1):
                left = left0 + str(j)
                if n % 2 == 0:
                    s = left + left[::-1]
                else:
                    s = left + left[:-1][::-1]
                if div7(s):
                    return s
            return 0
