class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9)
            return string(n, '9');
        if(k == 5){
            string s(n, '9');
            s.front() = s.back() = '5';
            return s;
        }
        if(k == 2){
            string s(n, '9');
            s.front() = s.back() = '8';
            return s;
        }
        if(k == 4){
            string s(n, '9');
            s[0] = s[n-1] = '8';
            if(n > 1)
                s[1] = s[n-2] = '8';
            return s;
        }
        if(k == 8){
            string s(n, '9');
            s[0] = s[n-1] = '8';
            if(n > 2)
                s[1] = s[n-2] = '8';
            if(n > 4)
                s[2] = s[n-3] = '8';
            return s;
        }
        if(k == 6){
            string s(n, '9');
            if(n == 1) {
                s[0] = '6';
            }
            else if(n % 2 == 1){
                s[0] = s[n-1] = s[n/2] = '8';
            }
            else{
                s[0] = s[n-1] = '8';
                s[n / 2 - 1] -= 2;
                s[n / 2] -= 2;
            }
            return s;
        }
        auto cal = [](string s, int mod){
            int now = 0;
            for(int i=s.size() - 1;i>=0;i--){
                now = (now * 10 + s[i] - '0') % mod;
            }
            return now;
        };
        if(k == 7){
            string s(n, '9');
            int r9[]{0, 2, 1, 5, 3, 4};
            while(cal(s, 7) != 0){
                s[n / 2]--;
                if(n % 2 == 0)
                    s[n / 2 - 1]--;
            }
            return s;
        }
        return {};
    }
};
