class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        ret = ["9"] * n
        mid = n // 2
        if k == 1:
            pass
        elif k == 2:
            ret[0] = "8"
            ret[-1] = "8"
        elif k == 3:
            pass
        elif k == 4:
            ret[0] = "8"
            if len(ret) > 1:
                ret[1] = "8"
                ret[-2] = "8"
            ret[-1] = "8"
        elif k == 5:
            ret[0] = "5"
            ret[-1] = "5"
        elif k == 6:
            if n == 1:
                ret[0] = "6"
            elif n == 2:
                ret[0] = "6"
                ret[1] = "6"
            else:
                ret[0] = "8"
                ret[-1] = "8"
                if n % 2 == 0:
                    ret[mid] = "7"
                    ret[mid-1] = "7"
                else:
                    ret[mid] = "8"
        elif k == 7:
            mod_7 = 0
            cur = [1]
            for i in range(n):
                mod_7 = (mod_7 + cur[-1]*9) % 7
                cur.append((cur[-1] * 10) % 7)
            ct = 0
            if n % 2 == 0:
                while (mod_7 != 0):
                    mod_7 -= cur[mid] + cur[mid - 1]
                    mod_7 += 70
                    mod_7 %= 7
                    ct += 1
                ret[mid] = str(9 - ct)
                ret[mid - 1] = str(9 - ct)
            elif n % 2 == 1:
                while (mod_7 != 0):
                    mod_7 -= cur[mid]
                    mod_7 += 70
                    mod_7 %= 7
                    ct += 1
                ret[mid] = str(9 - ct)
        elif k == 8:
            ret[0] = "8"
            if len(ret) > 1:
                ret[1] = "8"
                if len(ret) > 2:
                    ret[2] = "8"
                    ret[-3] = "8"
                ret[-2] = "8"
            ret[-1] = "8"
        elif k == 9:
            pass
        return "".join(ret)
