class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k == 1) return string(n, '9');
        int len = (n + 1) / 2;
        vector<int> pw(n), md(len);
        pw[0] = 1;
        for(int i = 1; i < n; ++i) pw[i] = 10 * pw[i - 1] % k;
        for(int i = 0; i < len; ++i) {
            if(i != n - 1 - i) md[i] = (pw[i] + pw[n - 1 - i]) % k;
            else md[i] = pw[i];
        }
        // for(int i = 0; i < len; ++i) cout << md[i] << " \n"[i == len - 1];
        vector<vector<int>> f(len + 1, vector<int>(k));
        f[len][0] = 1;
        for(int i = len - 1; i >= 0; --i) {
            for(int j = 0; j < k; ++j) {
                for(int u = 0; u < 10; ++u) {
                    if(f[i + 1][j]) {
                        f[i][(j + u * md[i] % k) % k] = 1;
                    }
                }
            }
        }
        string ans(n, '0');
        int rem = 0;
        for(int i = 0; i < len; ++i) {
            // cout << i << " " << rem << endl;
            for(int u = 9; u >= 0; --u) {
                if(f[i + 1][(rem - u * md[i] % k + k) % k]) {
                    ans[i] = ans[n - 1 - i] = char('0' + u);
                    rem = (rem - u * md[i] % k + k) % k;
                    break;
                }
            }
        }
        return ans;
    }
};
