class Solution {
    public String largestPalindrome(int n, int k) {
        int mod = 0;
        char[] ca = new char[n];
        for (int i = 0; i < n; ++i) {
            mod *= 10;
            mod += 9;
            ca[i] = '9';
            mod %= k;
        }
        if (n==1) {
            if (k==2||k==4||k==8)
                return "8";
            if (k>=5)
                return ""+(char)('0'+k);
            return "9";
        }
        if (n==2) {
            if (k==2||k==4||k==8)
                return "88";
            if (k>=5)
                return ""+(char)('0'+k)+(char)('0'+k);
            return "99";
        }
        if (mod==0) {
            return new String(ca);
        }
        if (k==5) {
            ca[0] = '5';
            ca[n-1] = '5';
            return new String(ca);
        }
        if (k==2) {
            ca[0] = '8';
            ca[n-1] = '8';
            return new String(ca);
        }
        if (k==4) {
            ca[0] = '8';
            ca[1] = '8';
            ca[n-2] = '8';
            ca[n-1] = '8';
            return new String(ca);
        }
        if (k==8) {
            ca[0] = '8';
            ca[1] = '8';
            ca[2] = '8';
            ca[n-3] = '8';
            ca[n-2] = '8';
            ca[n-1] = '8';
            return new String(ca);
        }
        if (k==6) {
            ca[0] = '8';
            ca[n-1] = '8';
            if (n%2==0) {
                ca[(n-1)/2] = '7';
                ca[n/2] = '7';
            } else {
                ca[n/2] = '8';
            }
            return new String(ca);
        }
        while (mod>0) {
            //System.out.println(ca[n/2]+" "+mod);
            ca[n/2]--;
            if (n%2==0) {
                ca[(n-1)/2]--;
            }
            mod = 0;
            for (int i = 0; i < n; ++i) {
                mod *= 10;
                mod += ca[i]-'0';
                mod %= k;
            }
        }
        return new String(ca);
    }
}
