class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return '9' * n
        elif k == 2:
            if n == 1:
                return '8'
            else:
                return '8' + '9' * (n - 2) + '8'
        elif k == 3:
            return '9' * n
        elif k == 4:
            if n <= 4:
                return '8' * n
            else:
                return '88' + '9' * (n - 4) + '88'
        elif k == 5:
            if n == 1:
                return '5'
            else:
                return '5' + '9' * (n - 2) + '5'
        elif k == 6:
            if n == 1:
                return '6'
            elif n == 2:
                return '66'
            elif n % 2 == 1:
                cnt = (n - 3) // 2
                return '8' + '9' * cnt + '8' + '9' * cnt + '8'
            else:
                cnt = (n - 4) // 2
                return '8' + '9' * cnt + '77' + '9' * cnt + '8'
        elif k == 7:
            for num in '987654321':
                if n % 2 == 1:
                    cnt = (n - 1) // 2
                    res = '9' * cnt + num + '9' * cnt
                    p7 = pow(10,cnt,7)
                    ans = (p7 - 1) * p7 * 10 + int(num) * p7 + p7 - 1
                    if ans % 7 == 0:
                        return res
                else:
                    cnt = (n - 2) // 2
                    res = '9' * cnt + num * 2 + '9' * cnt
                    p7 = pow(10,cnt,7)
                    ans = (p7 - 1) * p7 * 100 + int(num) * 11 * p7 + p7 - 1
                    if ans % 7 == 0:
                        return res
        elif k == 8:
            if n <= 6:
                return '8' * n
            else:
                return '888' + '9' * (n - 6) + '888'
        else:
            return '9' * n
