class Solution {
public:
    bool check(const string& num) {
        int cur = 0;
        for (int i = 0; i < num.size(); i++) {
            cur = cur * 10 + num[i] - '0';
            cur %= 7;
        }
        return cur == 0;
    }

    string largestPalindrome(int n, int k) {
        if (k == 1) {
            return string(n, '9');
        } else if (k == 2) {
            if (n == 1) return "8";
            return "8" + string(n - 2, '9') + "8";
        } else if (k == 3) {
            return string(n, '9');
        } else if (k == 4) {
            if (n <= 3) return string(n, '8');
            return "88" + string(n - 4, '9') + "88";
        } else if (k == 5) {
            if (n == 1) return "5";
            return "5" + string(n - 2, '9') + "5";
        } else if (k == 6) {
            if (n <= 2) return string(n, '6');
            if (n % 2 == 1) {
                string ret(n, ' ');
                ret[0] = '8';
                ret[n - 1] = '8';
                int cnt = 16;
                for (int i = 1, j = n - 2; i < j; i++, j--) {
                    ret[i] = ret[j] = '9';
                    cnt += 18;
                }
                for (int i = 9; i >= 0; i--) {
                    if ((cnt + i) % 3 == 0) {
                        ret[n / 2] = i + '0';
                        break;
                    }
                }
                return ret;
            } else {
                int c9 = (n - 4) / 2;
                return "8" + string(c9, '9') + "77" + string(c9, '9') + "8";
            }
        } else if (k == 7) {
            if (n <= 2) return string(n, '7');
            string ret;
            if (n % 2 == 0) {
                ret = string(n, '9');
                int mid = n / 2;
                for (int i = 9; i >= 0; i--) {
                    ret[mid] = '0' + i;
                    ret[mid - 1] = '0' + i;
                    if (check(ret)) {
                        return ret;
                    }
                }
                return "";
            } else {
                ret = string(n, '9');
                int mid = n / 2;
                for (int i = 9; i >= 0; i--) {
                    ret[mid] = '0' + i;
                    if (check(ret)) {
                        return ret;
                    }
                }
                return "";
            }
        } else if (k == 8) {
            if (n <= 5) return string(n, '8');
            return "888" + string(n - 6, '9') + "888";
        } else {
            return string(n, '9');
        }
        return "";
    }
};
