class Solution {
public:
    //find the largest palindrome that is divsible by k

    int dp[100001][10];
    int k;

    bool dfs(int idx,int rem,int n,vector<vector<int>>& mods){
        //4 stops at 2
        //3 stops at 2
        //2 stops at 1
        if(idx >= (n/2 + n%2)){
            return rem%k == 0;
        }
        if(dp[idx][rem] != -1)
            return dp[idx][rem];

        int o = n - idx - 1;
        int res = 0;
        for(int i = 0;i<10;i++){
            if(i == 0 && idx == 0)
                continue;
            int val = mods[i][idx] + mods[i][o];
            if(o == idx)
                val -= mods[i][o];
            res |= dfs(idx+1,(rem + val)%k,n,mods);
        }
        return dp[idx][rem] = res;
    }

      void dfs2(int idx,int rem,int n,vector<vector<int>>& mods,string& res){
        //4 stops at 2
        //3 stops at 1
        if(idx >= (n/2 + n%2))
            return;

        int o = n - idx - 1;
        for(int i = 9;i>=0;i--){
            if(i == 0 && idx == 0)
                continue;
            int val = mods[i][idx] + mods[i][o];
            if(o == idx)
                val -= mods[i][o];
            if(dfs(idx+1,(rem + val)%k,n,mods)){
                res[idx] = i + '0';
                res[o] = i + '0';
                dfs2(idx+1,(rem + val)%k,n,mods,res);
                return;
            }
        }
    }

    string largestPalindrome(int n, int k1) {
        vector<vector<int>> mods(10,vector<int>(n));
        memset(dp,-1,sizeof(dp));
        k = k1;

        for(int i = 1;i<10;i++){
            int curr = i;
            for(int j = n-1;j>=0;j--){
                mods[i][j] = curr;
                curr = (curr * 10)%k;
            }
        }
        dfs(0,0,n,mods);
        string res(n,'c');
        dfs2(0,0,n,mods,res);
        return res;
    }
};
