#include <bits/stdc++.h>
using namespace std;
#define SZ 366666
#define pb push_back
#define mp make_pair
#define fi first
#define se second
#define ll long long
#define pii pair<int,int>
#define vi vector<int>
#define all(x) (x).begin(),(x).end()
#define mem(a,b) memset(a,b,sizeof(a))
ll po(ll a,ll b) {
    ll x=1; while(b) {
        if(b&1) x=x*a;
        a=a*a; b>>=1;
    } return x;
}
class Solution {
public:
    string largestPalindrome(int n, int k, bool fs=0) {
        // kkkkkkk is clearly ok
        if(n<=6||fs) {
            int G=(n+1)/2;
            ll low=po(10,G-1),high=po(10,G)-1;
            for(ll hf=high;hf>=low;--hf) {
                string h=to_string(hf);
                string r=h; reverse(all(r));
                if(n&1) h.pop_back();
                string p=h+r;
                ll P=stoll(p);
                if(P%k==0) return p;
            }
            assert(0);
        }
        int F=999;
        while(1) {
            bool maybe=0;
            string RF=to_string(F); reverse(all(RF));
            int RFI=stoi(RF);
            for(int t=0;t<k;++t)
                if((RFI+1000*t)%k==0) {maybe=1; break;}
            if(maybe) break;
            --F;
        }
        string op(n,'9');
        for(int i=0;i<3;++i)
            op[i]=op[n-1-i]=to_string(F)[i];
        auto calc_rem=[&]() {
            ll rem=0;
            for(int i=0;i<n;++i) {
                rem=rem*10+(op[i]-'0');
                rem%=k;
            }
            return rem;
        };
        int mid=(n-1)/2;
        while(calc_rem()) {
            op[mid]--;
            if(n%2==0) op[mid+1]--;
        }
        return op;
    }
};
