class Solution {
public:

    bool check(string s) {
        int sum = 0;
        for(auto c: s) {
            int a = c - '0';
            sum *= 10;
            sum += a;
            sum %= 7;
        }
        return sum == 0;
    }

    string largestPalindrome(int n, int m) {
        string ans = "";
        if (m == 1) {
            for(int k=0;k<n;k++) ans  += '9';
        } else if (m == 2) {
            for(int k=0;k<n;k++) {
                if (k == 0 || k == n - 1) ans += '8';
                else ans += '9';
            }
        } else if (m == 3) {
            for(int k=0;k<n;k++) ans  += '9';
        } else if (m == 4) {
            for(int k=0;k<n;k++) {
                if (k <= 1 || k >= n - 2) ans += '8';
                else ans += '9';
            }
        } else if (m == 5) {
            for(int k=0;k<n;k++) {
                if (k == 0 || k == n - 1) ans += '5';
                else ans += '9';
            }
        } else if (m == 6) {
            if (n <= 2) {
                for(int k=0;k<n;k++) {
                    ans += '6';
                }
            } else {
                if (n & 1) {
                    for(int k=0;k<n;k++) {
                        if (k == 0 || k == n - 1) ans += '8';
                        else if (k == n / 2) ans += '8';
                        else ans += '9';
                    }
                } else {
                    for(int k=0;k<n;k++) {
                        if (k == 0 || k == n - 1) ans += '8';
                        else if (k == n / 2 || k == (n / 2 - 1)) ans += '7';
                        else ans += '9';
                    }
                }
            }
        } else if (m == 7) {
            if (n <= 2) {
                for(int k=0;k<n;k++) {
                    ans += '7';
                }
            } else {
                for(int k=0;k<n;k++) {
                    ans += '9';
                }
                for(int k=9;k>=0;k--) {
                    if (n & 1) {
                        ans[n / 2] = '0' + k;
                    } else {
                        ans[n / 2] = '0' + k;
                        ans[n / 2 - 1] = '0' + k;
                    }
                    if (check(ans)) break;
                }
            }
        } else if (m == 8) {
            for(int k=0;k<n;k++) {
                if (k <= 2 || k >= n - 3) ans += '8';
                else ans += '9';
            }
        } else if (m == 9) {
            for(int k=0;k<n;k++) ans  += '9';
        }
        return ans;
    }
};
