class Solution {
public:
    int mod(string bigNumber,int divisor)
    {
        int i,len,ans=0;
        len = bigNumber.size();
        for(i=0;i<len;i++)
        {
            ans     = ans*10 + bigNumber[i]-'0';   //前一位上的数的余数*10+当前位上的数
            ans = ans % divisor;
        }
        return ans;
    }
    string largestPalindrome(int n, int k) {
        string ans="";
        if(k==1 || k==3 || k==9){
            for(int i=0;i<n;i++){
                ans+="9";
            }
            return ans;
        }
        if(k==2){
            if(n==1){
                return "8";
            }
            ans+="8";
            for(int i=1;i<n-1;i++){
                ans+="9";
            }
            ans+="8";
            return ans;
        }
        if(k==4){
            if(n==1){
                return "8";
            }
            if(n==2){
                return "88";
            }
            if(n==3){
                return "888";
            }
            ans+="88";
            for(int i=2;i<n-2;i++){
                ans+="9";
            }
            ans+="88";
            return ans;
        }
        if(k==5){
            if(n==1){
                return "5";
            }
            ans+="5";
            for(int i=1;i<n-1;i++){
                ans+="9";
            }
            ans+="5";
            return ans;
        }
        if(k==6){
            if(n==1){
                return "6";
            }
            if(n==2){
                return "66";
            }
            if(n%2==1){
                ans+="8";
                for(int i=0;i<n/2-1;i++){
                    ans+="9";
                }
                ans+="8";
                for(int i=0;i<n/2-1;i++){
                    ans+="9";
                }
                ans+="8";
                return ans;
            }
            if(n%2==0){
                ans+="8";
                for(int i=1;i<n/2-1;i++){
                    ans+="9";
                }
                ans+="77";
                for(int i=1;i<n/2-1;i++){
                    ans+="9";
                }
                ans+="8";
                return ans;
            }
        }
        if(k==8){
            if(n<=6){
                for(int i=0;i<n;i++){
                    ans+="8";
                }
                return ans;
            }else{
                ans+="888";
                for(int i=3;i<n-3;i++){
                    ans+="9";
                }
                ans+="888";
                return ans;
            }
        }
        if(k==7){
            if(n==1){
                return "7";
            }
            if(n==2){
                return "77";
            }
            for(int i=0;i<n;i++){
                ans+="9";
            }
            while(mod(ans,7)!=0){
                if(n%2==1){
                    ans[n/2]--;
                }else{
                    ans[n/2]--;
                    ans[n/2-1]--;
                }
            }
            return ans;
        }
        return "0";
    }
};
