const int N = 50007;
class Solution {
public:
    bool vi[N][9];
    int h[100007];
    string s = "0123456789";
    int target;
    int n, k;
    bool dfs(int p, int v, string& res) {
        if (p == target) {
            return v == 0;
        }

        if (vi[p][v]) {
            return false;
        }
        vi[p][v] = true;

        int p1 = p, p2 = n - p - 1;
        if (p1 == p2) {
            for (int i = 9; i >= 0; --i) {
                res[p] = s[i];
                if (dfs(p + 1, (v + i * h[p]) % k, res)) {
                    return true;
                }
            }
            return false;
        }
        int r = (p == 0) ? 1 : 0;
        for (int i = 9; i >= r; --i) {
            res[p1] = res[p2] = s[i];
            if (dfs(p + 1, (v + i * (h[p1] + h[p2])) % k, res)) {
                return true;
            }
        }
        return false;
    }
    string largestPalindrome(int n_, int k_) {
        n = n_;
        k = k_;
        target = (n + 1) / 2;
        if (k == 1) {
            string res(n, '9');
            return res;
        }
        if (n == 1) {
            if (k == 3 || k == 1) return "9";
            if (k == 2 || k == 4) return "8";
            return to_string(k);
        }
        for (int i = 0, j = 1; i < n; ++i) {
            h[i] = (j % k);
            j = (j * 10) % k;
        }
        for (int i = 0; i < target; ++i) {
            for (int j = 0; j < 9; ++j) {
                vi[i][j] = false;
            }
        }
        string res(n, '#');
        dfs(0, 0, res);
        return res;
    }
};
