class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k==1 || k == 3 || k == 9) {
            string s ="";
            for (int i=0;i<n;i++) s+='9';
            return s;
        }
        if (k==2) {
            string s ="";
           s+="8"; for (int i=0;i<n-2;i++) s+='9';
            if (n>=2)  s+='8';
            return s;
        }
        if (k==5) {
            string s ="";
           s+="5"; for (int i=0;i<n-2;i++) s+='9';
            if (n>=2)  s+='5';
            return s;
        }
        if (k==4) {
            if (n==1) return "8";
            if (n==2) return "88";
            if (n==3) return "888";

            string s ="";
           s+="88"; for (int i=0;i<n-4;i++) s+='9';
            s+="88";
            return s;
        }
        if (k==8) {
            if (n==1) return "8";
            if (n==2) return "88";
            if (n==3) return "888";
            if (n==4) return "8888";
            if (n==5) return "88888";

            string s ="";
           s+="888"; for (int i=0;i<n-6;i++) s+='9';
            s+="888";
            return s;
        }
        if (k==6) {
            if (n==1) return "6";
        }
        vector <char> ans(n, '9');
        if (k==6) { ans[0] = '8'; ans[n-1] = '8'; }
        int mod = 0;
        for (int i=0;i<n;i++) {
            mod *= 10; mod += ans[i]-'0'; mod%=k;
        }
        if (mod==0) {
            string s = "";
            for (int i=0;i<n;i++) s+=ans[i];
            return s;
        }
        if (n%2==1) {
            int m1 = 1;
            for (int i=0;i<n/2;i++) {m1*=10; m1%=k;}
            while (mod!=0) {
                ans[n/2]--; mod-=m1; mod+=k * 100; mod%=k;
            }
            string s = "";
            for (int i=0;i<n;i++) s+=ans[i];
            return s;
        }
        if (n%2==0) {
            int m1 = 11;
            for (int i=0;i<n/2-1;i++) {m1*=10; m1%=k;}
            while (mod!=0) {
                ans[n/2]--; ans[n/2 - 1]--; mod-=m1; mod+=k * 100; mod%=k;
            }
            string s = "";
            for (int i=0;i<n;i++) s+=ans[i];
            return s;
        }
        return "";
    }
};
