class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n == 1:
            return '0989856789'[k]

        if k == 1 or k == 3 or k == 9:
            return '9' * n

        if k == 5:
            return '5' + '9' * (n - 2) + '5'

        if k == 2:
            return '8' + '9' * (n - 2) + '8'

        if k == 4:
            if n <= 4:
                return '8' * n
            return '88' + '9' * (n - 4) + '88'

        if k == 8:
            if n <= 6:
                return '8' * n
            return '888' + '9' * (n - 6) + '888'

        if k == 6:
            if n == 2:
                return '66'
            if n == 3:
                return '888'
            if n % 2 == 0:
                return '8' + '9' * ((n - 4) // 2) + '77' + '9' * ((n - 4) // 2) + '8'
            if n % 2 == 1:
                return '8' + '9' * ((n - 3) // 2) + '8' + '9' * ((n - 3) // 2) + '8'

        if k == 7:
            nn = n % 12
            side = (n - nn) // 2
            if nn == 0:
                return '9' * n

            if nn % 2 == 0:
                cand = ['9' * ((nn - 2) // 2) + str(i) * 2 + '9' * ((nn - 2) // 2) for i in range(9, -1, -1)]
                for c in cand:
                    if int(c) % 7 == 0:
                        return '9' * side + c + '9' * side

            if nn % 2 == 1:
                cand = ['9' * ((nn - 1) // 2) + str(i) + '9' * ((nn - 1) // 2) for i in range(9, -1, -1)]
                for c in cand:
                    if int(c) % 7 == 0:
                        return '9' * side + c + '9' * side
