class Solution {
    public String largestPalindrome(int n, int k) {
        char[] chars = new char[n];
        Arrays.fill(chars, '9');
        if (k == 1 || k == 3 || k == 9) {

        } else if (k == 2) {
            chars[0] = '8';
            chars[n - 1] = '8';
        } else if (k == 4) {
            if (n == 1) {
                return "8";
            }
            chars[0] = '8';
            chars[1] = '8';
            chars[n - 1] = '8';
            chars[n - 2] = '8';
        } else if (k == 5) {
            chars[0] = '5';
            chars[n - 1] = '5';
        } else if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";
            }
            chars[0] = '8';
            chars[n - 1] = '8';
            if (n % 2 == 1) {
                chars[n / 2] = '8';
            } else {
                chars[n / 2] = '7';
                chars[n / 2 - 1] = '7';
            }
        } else if (k == 7) {
            if (n == 1) {
                return "7";
            }
            if (n == 2) {
                return "77";
            }
            if (n == 3) {
                return "959";
            }
            if (n == 4) {
                return "9779";
            }
            int[] arr = {0, 2, 1, 5, 3, 4};
            int t = arr[n % 6];
            int[] arr2 = {1, 3, 2, 6, 4, 5};
            if (n % 2 == 1) {
                int t2 = arr2[(n / 2) % 6];
                for (int i = 0; i < 10; i++) {
                    if ((t - t2 * i) % 7 == 0) {
                        chars[n / 2] = (char) ('0' + (9 - i));
                        break;
                    }
                }
            } else {
                int t2 = arr2[(n / 2) % 6];
                int t3 = arr2[(n / 2 - 1) % 6];
                for (int i = 0; i < 10; i++) {
                    if ((t - (t2 * i + t3 * i)) % 7 == 0) {
                        chars[n / 2] = (char) ('0' + (9 - i));
                        chars[n / 2 - 1] = (char) ('0' + (9 - i));
                        break;
                    }
                }
            }
        } else if (k == 8) {
            if (n == 1) {
                return "8";
            }
            if (n == 2) {
                return "88";
            }
            chars[0] = '8';
            chars[1] = '8';
            chars[2] = '8';
            chars[n - 1] = '8';
            chars[n - 2] = '8';
            chars[n - 3] = '8';
        }
        return new String(chars);
    }
