bool CheckDivisibleBy7(string &number) {
    int remainder = 0;
    for (char c : number) {
        remainder = ((remainder << 3) + (remainder << 1) + (c - '0')) % 7;
    }
    return !remainder;
}

class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }
        if (k == 2) {
            if (n >= 2) {
                return "8" + string(n - 2, '9') + "8";
            }
            return "8";
        }
        if (k == 5) {
            if (n >= 2) {
                return "5" + string(n - 2, '9') + "5";
            }
            return "5";
        }
        if (k == 4) {
            if (n < 4) {
                return string(n, '8');
            }
            return "88" + string(n - 4, '9') + "88";
        }
        if (k == 8) {
            if (n < 6) {
                return string(n, '8');
            }
            return "888" + string(n - 6, '9') + "888";
        }
        if (k == 6) {
            if (n < 3) {
                return string(n, '6');
            }
            if (n & 1) {
                return "8" + string((n - 3) >> 1, '9') + "8" + string((n - 3) >> 1, '9') + "8";
            }
            return "8" + string((n - 4) >> 1, '9') + "77" + string((n - 4) >> 1, '9') + "8";
        }
        if (n < 2) {
            return string(n, '7');
        }
        string buffer = string(n, '9');
        if (n & 1) {
            for (char c = '9'; c >= '0'; --c) {
                buffer[n >> 1] = c;
                if (CheckDivisibleBy7(buffer)) {
                    break;
                }
            }
        }
        else {
            for (char c = '9'; c >= '0'; --c) {
                buffer[(n >> 1) - 1] = buffer[n >> 1] = c;
                if (CheckDivisibleBy7(buffer)) {
                    break;
                }
            }
        }
        return buffer;
    }
};
