class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return "9" * n
        elif k == 2:
            if n == 1:
                return "8"
            return "8" + "9" * (n-2) + "8"
        elif k == 3:
            return "9" * n
        elif k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + "9" * (n-4) + "88"
        elif k == 5:
            if n == 1:
                return "5"
            return "5" + "9" * (n-2) + "5"
        elif k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n == 3:
                return "888"
            if n % 2 == 0:
                return "8" + "9" * (n//2-2) + "77" + "9" * (n//2-2) + "8"
            else:
                return "8" + "9" * (n//2-1) + "8" + "9" * (n//2-1) + "8"
            return "9" * n
        elif k == 7:
            if n == 1:
                return "7"
            elif n == 2:
                return "77"
            elif n == 3:
                return "959"
            if n % 2 == 0:
                for mid in range(9, -1, -1):
                    s = "9" * (n//2-1) + str(mid) * 2 + "9" * (n//2-1)
                    xs = [1, 3, 2, -1, -3, -2]
                    xi = 0
                    c = 0
                    for i in range(len(s)-1, -1, -1):
                        c += xs[xi] * int(s[i])
                        xi += 1
                        xi %= 6
                    if (c) % 7 == 0:
                        return "9" * (n//2-1) + str(mid) * 2 + "9" * (n//2-1)
            else:
                for mid in range(9, -1, -1):
                    s = "9" * (n//2) + str(mid) + "9" * (n//2)
                    xs = [1, 3, 2, -1, -3, -2]
                    xi = 0
                    c = 0
                    for i in range(len(s)-1, -1, -1):
                        c += xs[xi] * int(s[i])
                        xi += 1
                        xi %= 6
                    if (c) % 7 == 0:
                        return "9" * (n//2) + str(mid) + "9" * (n//2)
        elif k == 8:
            if n <= 6:
                return "8" * n
            return "888" + "9" * (n-6) + "888"
        else:
            return "9" * n
