bool check(string& s, int k)
{
    int n = sz(s);
    int c = 0;
    for (int i = 0; i < n; i++)
    {
        c = (c * 10 + s[i] - '0') % k;
    }
    return c == 0;
}

class Solution
{
public:
    string largestPalindrome(int n, int k)
    {
        string s = "";
        if (k == 1 || k == 3 || k == 9)
        {
            for (int i = 1; i <= n; i++) s += '9';
        }
        if (k == 2)
        {
            if (n == 1) return "8";
            if (n == 2) return "88";
            s += '8';
            for (int i = 1; i <= n - 2; i++) s += '9';
            s += '8';
        }
        if (k == 4)
        {
            if (n == 1) return "8";
            if (n == 2) return "88";
            if (n == 3) return "888";
            if (n == 4) return "8888";
            s += "88";
            for (int i = 1; i <= n - 4; i++) s += '9';
            s += "88";
        }
        if (k == 8)
        {
            if (n == 1) return "8";
            if (n == 2) return "88";
            if (n == 3) return "888";
            if (n == 4) return "8888";
            if (n == 5) return "88888";
            if (n == 6) return "888888";
            s += "888";
            for (int i = 1; i <= n - 6; i++) s += '9';
            s += "888";
        }
        if (k == 5)
        {
            if (n == 1) return "5";
            if (n == 2) return "55";
            s += '5';
            for (int i = 1; i <= n - 2; i++) s += '9';
            s += '5';
        }
        if (k == 6)
        {
            if (n == 1) return "6";
            if (n == 2) return "66";
            // if (n == 3) return "888";
            // if (n == 4) return "8778";
            // if (n == 5) return "89898";
            // if (n == 6) return "897798";

            if (n % 2 == 1)
            {
                s += '8';
                int q = (n - 3) / 2;
                for (int i = 1; i <= q; i++) s += '9';
                s += '8';
                for (int i = 1; i <= q; i++) s += '9';
                s += '8';
            }
            else
            {
                s += '8';
                int q = (n - 4) / 2;
                for (int i = 1; i <= q; i++) s += '9';
                s += "77";
                for (int i = 1; i <= q; i++) s += '9';
                s += '8';
            }
        }
        if (k == 7)
        {
            if (n == 1) return "7";
            if (n == 2) return "77";

            if (n % 2 == 1)
            {
                int q = (n - 1) / 2;

                for (int m = 9; m >= 0; m--)
                {
                    string cur = "";
                    for (int i = 1; i <= q; i++) cur += '9';
                    cur += '0' + m;
                    for (int i = 1; i <= q; i++) cur += '9';

                    if (check(cur, 7))
                    {
                        s = cur;
                        break;
                    }
                }
            }
            else
            {
                int q = (n - 2) / 2;

                for (int m = 9; m >= 0; m--)
                {
                    string cur = "";
                    for (int i = 1; i <= q; i++) cur += '9';
                    cur += '0' + m;
                    cur += '0' + m;
                    for (int i = 1; i <= q; i++) cur += '9';

                    if (check(cur, 7))
                    {
                        s = cur;
                        break;
                    }
                }
            }
        }
        return s;
    }
};
