class Solution:
    def test(self, n: int, k: int) -> str:
        def isPalindrome(s):
            l = 0
            r = len(s) - 1
            while l < r:
                if s[l] != s[r]:
                    return False
                l += 1
                r -= 1
            return True

        start = int("9" * n)
        start -= start % k
        for num in range(start, 0, -k):
            if isPalindrome(str(num)):
                return num

    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        if k == 2:
            if n == 1:
                return "8"
            else:
                return "8" + ("9" * (n - 2)) + "8"
        if k == 4:
            if n <= 3:
                return "8" * n
            else:
                return "88" + ("9" * (n - 4)) + "88"
        if k == 5:
            if n == 1:
                return "5"
            else:
                return "5" + ("9" * (n - 2)) + "5"
        if k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + ("9" * (n - 6)) + "888"
        if k == 6:
            if n <= 2:
                return "6" * n
            elif n == 3:
                return "888"
            elif n == 4:
                return "8778"
            elif n % 2 == 0:
                return (
                    "8" + ("9" * ((n - 4) // 2)) + "77" + ("9" * ((n - 4) // 2)) + "8"
                )
            else:
                return "8" + ("9" * ((n - 3) // 2)) + "8" + ("9" * ((n - 3) // 2)) + "8"
        if k == 7:
            arr = [9, 7, 7, 5, 7, 7, 9, 4, 4, 6, 4, 4]
            if n <= 2:
                return "7" * n
            elif n % 2 == 1:
                return "9" * ((n - 1) // 2) + str(arr[n % 12]) + "9" * ((n - 1) // 2)
            else:
                return (
                    "9" * ((n - 2) // 2) + str(arr[n % 12]) * 2 + "9" * ((n - 2) // 2)
                )
