class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in (1,3,9):
            return '9'*n
        if k==2:
            if n==1:
                return '8'
            return '8'+'9'*(n-2)+'8'
        if k==4:
            if n<4:
                return '8'*n
            return '88'+'9'*(n-4)+'88'
        if k==8:
            if n<6:
                return '8'*n
            return '888'+'9'*(n-6)+'888'
        if k==5:
            if n==1:
                return '5'
            return '5'+'9'*(n-2)+'5'
        if k==6:
            if n<3:
                return '6'*n
            if n%2:
                return '8'+'9'*((n-3)//2)+'8'+'9'*((n-3)//2)+'8'
            return '8'+'9'*((n-4)//2)+'77'+'9'*((n-4)//2)+'8'
        if k==7:
            if n<3:
                return '7'*n
            if n==3:
                return '959'
            if n%6==0:
                return '9'*n
            if n==4:
                return '9779'
            if n==5:
                return '99799'

            base=[0,2,1,5,3,4]
            start=base[n%6]
            if n%2:
                times=base[(n//2)%6]+1
                for i in range(10):
                    if (start-times*i)%7==0:
                        return '9'*(n//2)+str(9-i)+'9'*(n//2)
            times=base[(n//2-1)%6]*11+11
            for i in range(10):
                if (start-times*i)%7==0:
                    return '9'*(n//2-1)+str(9-i)*2+'9'*(n//2-1)
