class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans = "";
        if(k == 1) {
            for(int i=0; i<n; i++) ans += "9";
            return ans;
        }
        if(k == 2) {
            ans += "8";
            if(n == 1) return ans;
            for(int i=1; i<n-1; i++) ans += "9";
            ans += "8";
            return ans;
        }
        if(k == 3) {
            for(int i=0; i<n; i++) {
                ans += "9";
            }
            return ans;
        }
        if(k == 4) {
            if(n == 1) return "8";
            if(n == 2) return "88";
            if(n == 3) return "888";
            if(n == 4) return "8888";
            ans += "88";
            for(int i=2; i<n-2; i++) ans += "9";
            ans += "88";
            return ans;
        }
        if(k == 5) {
            ans += "5";
            if(n == 1) return ans;
            for(int i=1; i<n-1; i++) ans += "9";
            ans += "5";
            return ans;
        }
        if(k == 6) {
            if(n == 1) return "6";
            if(n == 2) return "66";
            ans += "8";
            int sum = 0;
            sum += 16;
            for(int i=1; i<(n-1)/2; i++) {
                ans += "9";
                sum += 18;
            }

            for(int i=9; i>=1; i--) {
                if(n % 2) {
                    if((sum + i) % 3 == 0) {
                        char c = i+'0';
                        ans += c;
                        break;
                    }
                }
                else {
                    if((sum + 2*i) % 3 == 0) {
                        char c = i+'0';
                        ans += c;
                        ans += c;
                        break;
                    }
                }
            }

            for(int i=1; i<(n-1)/2; i++) ans += "9";
            ans += "8";
            return ans;
        }
        if(k == 7) {
            if(n == 1) return "7";
            if(n == 2) return "77";
            vector<int>temp(n+1,9);
            if(n % 2) {
                for(int j=9; j>=0; j--) {
                    temp[n/2+1] = j; 
                    int rem = 0;
                    for(int j=1; j<=n; j++) {
                        rem = rem * 10 + temp[j];
                        rem  = (rem % 7);
                    }
                    if(rem == 0) {

                        for(int j=1; j<=n; j++) {
                            char c = '0'+temp[j];
                            ans += c;
                        }
                        return ans;
                    }
                } 
            }
            else {
                for(int j=9; j>=0; j--) {
                    temp[n/2] = j; 
                    temp[n/2+1] = j;
                    int rem = 0;
                    for(int j=1; j<=n; j++) {
                        rem = rem * 10 + temp[j];
                        rem  = (rem % 7);
                    }
                    if(rem == 0) {
                        for(int j=1; j<=n; j++) {
                            char c = '0'+temp[j];
                            ans += c;
                        }
                        return ans;
                    }
                } 
            }

        }
        if(k == 8) {
            if(n <= 6) {
                for(int i=0; i<n; i++) ans += "8";
                return ans;
            }

            for(int i=0; i<3; i++) ans += "8";
            for(int i=3; i<n-3; i++) ans += "9";
            for(int i=0; i<3; i++) ans += "8";
            return ans;
        }
        if(k == 9) {
            for(int i=0; i<n; i++) ans += "9";
            return ans;
        }
        return "0";
    }
};
