        Rep(i,m){
            if(i+1<m){
                For(j,k)if(~fa[i+1][j])For(l,10)ckmax(fa[i][(j+l*(pw[i]+pw[n-1-i]))%k],l);
            }
            else{
                FOR(l,1,10)fa[i][l*(pw[i]+((n&1)?0:pw[n-1-i]))%k]=l;
            }
        }
        assert(~fa[0][0]);
        string ret;
        ret+=char(fa[0][0]|48);
