            j-=l*(pw[i]+(((n&1)&&i+1==m)?0:pw[n-1-i]));
            j=(j%k+k)%k;
            ret+=char(fa[i+1][j]|48);
        }
        Rep(i,n>>1)ret+=ret[i];
