public class Solution {
    public string LargestPalindrome(int n, int k) {
        StringBuilder sb = new();
        if (k == 1 || k == 3 || k == 9)
        {
            for (int i = 0; i < n; i++)
            {
                sb.Append('9');
            }
            return sb.ToString();
        }
        else if (k == 2)
        {
            if (n == 1)
            {
                return "8";
            }
            sb.Append('8');
            for (int i = 2; i < n; i++)
            {
                sb.Append('9');
            }
            sb.Append('8');
            return sb.ToString();
        }
        else if (k == 4)
        {
            if (n == 1)
            {
                return "8";
            }
            if (n == 2)
            {
                return "88";
            }
            if (n == 3)
            {
                return "888";
            }
            sb.Append("88");
            for (int i = 4; i < n; i++)
            {
                sb.Append('9');
            }
            sb.Append("88");
            return sb.ToString();
        }
        else if (k == 5)
        {
            if (n == 1)
            {
                return "5";
            }
            sb.Append('5');
            for (int i = 2; i < n; i++)
            {
                sb.Append('9');
            }
            sb.Append('5');
            return sb.ToString();
        }
        else if (k == 6)
        {
            if (n == 1)
            {
                return "6";
            }
            if (n == 2)
            {
                return "66";
            }
            if (n % 2 == 0)
            {
                int s = (n - 4) / 2;
                sb.Append('8');
                for (int i = 0; i < s; i++)
                {
                    sb.Append('9');
                }
                sb.Append("77");
                for (int i = 0; i < s; i++)
                {
                    sb.Append('9');
                }
                sb.Append('8');
                return sb.ToString();
            }
            else
            {
                int s = (n - 3) / 2;
                sb.Append('8');
                for (int i = 0; i < s; i++)
                {
                    sb.Append('9');
                }
                sb.Append("8");
                for (int i = 0; i < s; i++)
                {
                    sb.Append('9');
                }
                sb.Append('8');
                return sb.ToString();
            }
            // return "66" or "89898" or "897798";
        }
        else if (k == 7)
        {
            int s = n % 12;
            if (s == 0)
            {
                s += 12;
            }
            List<string> middles = [
                "",
                "7",
                "77",
                "959",
                "9779",
                "99799",
                "999999",
                "9994999",
                "99944999",
                "999969999",
                "9999449999",
                "99999499999",
                "999999999999"
            ];
            for (int i = 0; i < ((n-s) / 2); i++)
            {
                sb.Append('9');
            }
            sb.Append(middles[s]);
            for (int i = 0; i < ((n-s) / 2); i++)
            {
                sb.Append('9');
            }
            return sb.ToString();
            // return "7" or "77" or "959" or "9779" or "99799" or "999999" or "9994999" or "99944999" or "999969999" or "9999449999" or "99999499999" or "999999999999" or "9999997999999"
        }
        else if (k == 8)
        {
            if (n <= 6)
            {
                for (int i = 0; i < n; i++)
                {
                    sb.Append('8');
                }
                return sb.ToString();
            }
            else
            {
                sb.Append("888");
                for (int i = 6; i < n; i++)
                {
                    sb.Append('9');
                }
                sb.Append("888");
                return sb.ToString();
            }
            // return "8" or "88" or "8889999888";
        }
        else
        {
            return "";
        }
    }
}
