class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3:
            return "9"*n
        if k == 2:
            if n == 1:
                return "8"
            return "8"+"9"*(n-2)+"8"
        if k == 4:
            if n <= 4:
                return "8"*n
            a = (n-4)
            b = (n-a)//2
            return "8"*b+"9"*a+"8"*b
        if k == 5:
            if n == 1:
                return "5"
            return "5"+"9"*(n-2)+"5"
        if k == 6:
            if n <= 2:
                return "6"*n
            if n == 3:
                return "888"
            if n == 4:
                return "8778"
            if n & 1:
                cnt = (n-5)//2+1
                return "8"+"9"*cnt+"8"+"9"*cnt+"8"
            else:
                cnt = (n-6)//2+1
                return "8"+"9"*cnt+"77"+"9"*cnt+"8"
        if k == 7:
            if n <= 2:
                return "7"*n
            select = ["9", "7", '6', '5', '4']

            def ok(s):
                x = 7
                g = 0
                for i in s:
                    g = (g*10+int(i)) % x
                return not g
            if n & 1:
                sz = n//2
                for c in select:
                    now = "9"*sz+c+"9"*sz
                    if ok(now):
                        return now
            else:
                sz = (n-1)//2
                for c in select:
                    now = "9"*sz+c+c+"9"*sz
                    if ok(now):
                        return now
        if k == 8:
            if n <= 6:
                return "8"*n
            a = n-6
            b = (n-a)//2
            return "8"*b+"9"*a+"8"*b
        return "9"*n
